import { createStore, applyMiddleware } from 'redux'
import { composeWithDevTools } from 'redux-devtools-extension'
import cookie from 'next-cookies'

const initialState = {
    user: cookie.get('user')
}

const reducer = (state = initialState, action) => {
    // switch (action.type) {
    //     case 'TICK':
