    //         ...state,
    //         count: state.count + 1
    //     }
    //     case 'DECREMENT':
    //     return {
    //         ...state,
    //         count: state.count - 1
    //     }
    //     case 'RESET':
    //     return {
    //         ...state,
    //         count: initialState.count
    //     }
    //     default:
    //     return state
    // }
    return state
}

export const initializeStore = (preloadedState = initialState) => {
    return createStore(
        reducer,
        preloadedState,
        composeWithDevTools(applyMiddleware())
    )
}
